import styled from "styled-components";
import { typeScale, primaryFont } from "../../utils";

export const PrimaryNavButton = styled.button`
  width: 80px;
  height: 80px;

  position: relative;

  color: ${props => props.theme.textColorOnPrimary};
  background-color: transparent;

  border: none;
  border-radius: 50%;

  box-shadow: 5px 5px 9px 0px rgba(174, 174, 192, 0.4), -5px -5px 9px 0px rgba(255, 255, 255, 1);
  display: flex;

  align-items: center;
  justify-content: center;

  overflow: hidden;

  cursor: pointer;

  font-family: ${primaryFont};
  font-size: ${typeScale.paragraph};

  transition: box-shadow 0.1s linear;

  &:disabled {
    background-color: ${props => props.theme.disabled};
    color: ${props => props.theme.textOnDisabled};
    cursor: not-allowed;
  }

  &:focus {
    outline: none;
  }

  &:active {
    box-shadow: 1px 1px 2px 0px rgba(174, 174, 192, 0.2), -1px -1px 1px 0px rgba(255, 255, 255, 0.7);
  }

  &::before {
    content: "";

    position: absolute;
    top: 2px;
    bottom: 2px;
    left: 2px;
    right: 2px;

    background-color: #f0f0f3;

    border-radius: 50%;
  }

  &::after {
    content: "";
    position: absolute;
    z-index: -1;
    top: 0px;
    bottom: 0px;
    left: 0px;
    right: 0px;

    background-color: #85ffbd;
    background-image: linear-gradient(45deg, #85ffbd 0%, #fffb7d 100%);

    /* transform: rotate(-90deg) translate(-50%, -100%); */
    transform: translate(-100%);
    transform-origin: top left;
    transition: transform 200ms;
  }

  &:hover::after {
    /* transform: rotate(0) translate(0, 0); */
    transform: translate(0);
  }
`;
