import React from "react";
import styled from "styled-components";
import { PrimaryButton } from "../../components/Buttons/Buttons";
import { Howl } from "howler";
import wav from "./studio-drums-1/421__tictacshutup__prac-hat-2.wav";
import wav2 from "./studio-drums-1/422__tictacshutup__prac-hat-3.wav";

export const PadGrid = styled.div`
  margin: 15px;
  display: grid;
  grid-template-columns: 1fr 1fr 1fr;
  column-gap: 15px;
  row-gap: 15px;
`;

export default function Pad() {
  const [mouseDown, setMouseDown] = React.useState(false);
  const keys = [
    {
      letter: "a",
      keyCode: 65,
      sound: new Howl({
        src: [wav],
      }),
    },
    {
      letter: "z",
      keyCode: 90,
      sound: new Howl({
        src: [wav2],
      }),
    },
    {
      letter: "e",
      keyCode: 69,
      sound: new Howl({
        src: [wav2],
      }),
    },
    {
      letter: "q",
      keyCode: 81,
      sound: new Howl({
        src: [wav2],
      }),
    },
    {
      letter: "s",
      keyCode: 83,
      sound: new Howl({
        src: [wav2],
      }),
    },

    {
      letter: "d",
      keyCode: 68,
      sound: new Howl({
        src: [wav2],
      }),
    },
    {
      letter: "w",
      keyCode: 87,
      sound: new Howl({
        src: [wav2],
      }),
    },
    {
      letter: "x",
      keyCode: 88,
      sound: new Howl({
        src: [wav2],
      }),
    },
    {
      letter: "c",
      keyCode: 67,
      sound: new Howl({
        src: [wav2],
      }),
    },
  ];

  const onKeyDown = e => {
    if (e.repeat) return;
    const key = keys.find(key => key.keyCode === e.keyCode);
    key.sound.play();
  };
  const onKeyUp = () => {
    // console.log(e.keyCode);
  };

  React.useEffect(() => {
    window.addEventListener("keydown", onKeyDown);
    window.addEventListener("keyup", onKeyUp);

    return () => {
      window.removeEventListener("keydown", onKeyDown);
      window.removeEventListener("keyup", onKeyUp);
    };
  });

  return (
    <PadGrid tabIndex="0" onMouseDown={() => setMouseDown(true)}>
      {keys.map(key => (
        <PrimaryButton
          key={key.letter}
          onMouseDown={() => key.sound.play()}
          onMouseEnter={() => {
            console.log(mouseDown);
            if (mouseDown) key.sound.play();
          }}
        >
          {key.letter}
        </PrimaryButton>
      ))}
    </PadGrid>
  );
}
