import React from "react";
import styled from "styled-components";
import { PrimaryButton } from "../../components/Buttons/Buttons";
import { Howl } from "howler";

import wav5 from "./studio-drums-1/435__tictacshutup__prac-ride-bell.wav";
import wav6 from "./studio-drums-1/436__tictacshutup__prac-ride.wav";
import wav7 from "./studio-drums-1/448__tictacshutup__prac-tom-light.wav";
import wav8 from "./studio-drums-1/445__tictacshutup__prac-snare-roll-short.wav";
import wav9 from "./studio-drums-1/434__tictacshutup__prac-ride-bell-loud.wav";

export const PadGrid = styled.div`
  margin: 15px;
  display: grid;
  grid-template-columns: 1fr 1fr 1fr;
  column-gap: 15px;
  row-gap: 15px;
`;

export default function Pad() {
  const [mouseDown, setMouseDown] = React.useState(false);
  const keys = [
    {
      letter: "a",
      keyCode: 65,
      sound: new Howl({
        src: [
          "https://freesound.org/people/Migfus20/sounds/560446/download/560446__migfus20__happy-background-music.mp3",
        ],
      }),
    },
    {
      letter: "z",
      keyCode: 90,
      sound: new Howl({
        src: ["https://www.dropbox.com/h?preview=426__tictacshutup__prac-hat.wav"],
      }),
    },
    {
      letter: "e",
      keyCode: 69,
      sound: new Howl({
        src: ["https://www.dropbox.com/h?preview=426__tictacshutup__prac-hat.wav"],
      }),
    },
    {
      letter: "q",
      keyCode: 81,
      sound: new Howl({
        src: [
          "https://freesound.org/people/sribubba/sounds/130153/download/130153__sribubba__circuit-breaker-2-dp.wav",
        ],
      }),
    },
    {
      letter: "s",
      keyCode: 83,
      sound: new Howl({
        src: [wav5],
      }),
    },

    {
      letter: "d",
      keyCode: 68,
      sound: new Howl({
        src: [wav6],
      }),
    },
    {
      letter: "w",
      keyCode: 87,
      sound: new Howl({
        src: [wav7],
      }),
    },
    {
      letter: "x",
      keyCode: 88,
      sound: new Howl({
        src: [wav8],
      }),
    },
    {
      letter: "c",
      keyCode: 67,
      sound: new Howl({
        src: [wav9],
      }),
    },
  ];

  const onKeyDown = e => {
    if (e.repeat) return;
    const key = keys.find(key => key.keyCode === e.keyCode);
    key.sound.play();
  };
  const onKeyUp = () => {
    // console.log(e.keyCode);
  };

  React.useEffect(() => {
    window.addEventListener("keydown", onKeyDown);
    window.addEventListener("keyup", onKeyUp);

    return () => {
      window.removeEventListener("keydown", onKeyDown);
      window.removeEventListener("keyup", onKeyUp);
    };
  });

  return (
    <PadGrid
      tabIndex="0"
      onMouseDown={() => setMouseDown(true)}
      onMouseUp={() => setMouseDown(false)}
    >
      {keys.map(key => (
        <PrimaryButton
          key={key.letter}
          onMouseDown={() => key.sound.play()}
          onMouseEnter={() => {
            if (mouseDown) key.sound.play();
          }}
        >
          {key.letter}
        </PrimaryButton>
      ))}
    </PadGrid>
  );
}
