import React from "react";
import styled from "styled-components";
import { typeScale, primaryFont } from "../../utils";

const Input = styled.input`
  width: 316px;
  height: 52px;
  background-color: ${props => props.theme.textFieldBackground};
  border: none;
  padding-left: 8px;
  font-family: ${primaryFont};
  border-radius: 2px;
`;

const Label = styled.label`
  color: ${props => props.theme.textFieldLabelColor};
  font-size: ${typeScale.helperText};
  margin-bottom: 8px;
`;

export const EmailInput = ({ label, placeholder, setEmail = console.log() }) => (
  <div style={{ display: "flex", flexDirection: "column", marginTop: "16px" }}>
    <Label htmlFor="email">{label}</Label>
    <Input
      id="email"
      type="email"
      placeholder={placeholder}
      onChange={e => setEmail(e.target.value)}
    />
  </div>
);

export const PasswordInput = ({ label, placeholder, value }) => (
  <div style={{ display: "flex", flexDirection: "column", marginTop: "16px" }}>
    <Label htmlFor="password">{label}</Label>
    <Input
      id="password"
      type="password"
      placeholder={placeholder}
      value={value?.email}
      onChange={e => value.setEmail(e.target.value)}
    />
  </div>
);
