import React from "react";
import styled from "styled-components";
import { PrimaryButton } from "../../components/Buttons/Buttons";
import { Howl } from "howler";
import wav from "./studio-drums-1/421__tictacshutup__prac-hat-2.wav";
import wav2 from "./studio-drums-1/422__tictacshutup__prac-hat-3.wav";

export const PadGrid = styled.div`
  margin: 15px;
  display: grid;
  grid-template-columns: 1fr 1fr 1fr;
  column-gap: 15px;
  row-gap: 15px;
`;

export default function Pad() {
  const keys = {
    a: {
      sound: new Howl({
        src: [wav],
      }),
    },
    z: {
      sound: new Howl({
        src: [wav2],
      }),
    },
  };
  return (
    <PadGrid>
      {Object.keys(keys).map(key => (
        // <PrimaryButton key={key} onClick={() => sound.play()} />
        <PrimaryButton key={key} onClick={e => console.log(e.keyCode)} />
      ))}
    </PadGrid>
  );
}
