import styled from "styled-components";
import { typeScale, primaryFont } from "../../utils";

export const PrimaryNavButton = styled.button`
  width: 80px;
  height: 80px;
  background-color: #f0f0f3;
  color: ${props => props.theme.textColorOnPrimary};
  border: 1px solid transparent;
  border-radius: 50%;
  box-shadow: 10px 10px 30px 0px rgba(174, 174, 192, 0.4),
    -10px -10px 30px 0px rgba(255, 255, 255, 1);

  cursor: pointer;
  font-family: ${primaryFont};
  font-size: ${typeScale.paragraph};
  transition: box-shadow 500ms;

  &:disabled {
    background-color: ${props => props.theme.disabled};
    color: ${props => props.theme.textOnDisabled};
    cursor: not-allowed;
  }

  &:focus {
    outline: none;
    outline-offset: 2px;
  }

  &:active {
    box-shadow: 1px 1px 2px 0px rgba(174, 174, 192, 0.2) inset,
      -1px -1px 1px 0px rgba(255, 255, 255, 0.7) inset;
  }
`;
